(function() {
  const GRID_WIDTH = 26;
  const GRID_HEIGHT = 20;

  // Initial purple triangles (from first animation)
  const initialPurpleTriangles = [(1, 9), (2, 3), (2, 8), (2, 10), (2, 21), (3, 2), (3, 4), (3, 7), (3, 9), (3, 11), (3, 20), (3, 22), (4, 1), (4, 3), (4, 5), (4, 19), (4, 21), (4, 23), (5, 13), (6, 12), (6, 13), (7, 11), (7, 12), (7, 13), (8, 2), (8, 10), (8, 11), (8, 12), (8, 13), (8, 23), (9, 1), (9, 3), (9, 9), (9, 10), (9, 11), (9, 12), (9, 13), (9, 22), (9, 24), (10, 0), (10, 2), (10, 4), (10, 8), (10, 9), (10, 10), (10, 11), (10, 12), (10, 13), (10, 21), (10, 23), (10, 25), (11, 0), (11, 1), (11, 3), (11, 5), (11, 7), (11, 8), (11, 9), (11, 10), (11, 11), (11, 12), (11, 13), (11, 20), (11, 22), (11, 24), (11, 25), (15, 3), (15, 21), (16, 2), (16, 4), (16, 20), (16, 22), (17, 1), (17, 3), (17, 5), (17, 9), (17, 19), (17, 21), (17, 23), (18, 8), (18, 10), (19, 7), (19, 9), (19, 11)];

  // Target perfect upside-down triangle in center
  function isTargetCell(row, col) {
    const centerRow = 9;
    const centerCol = 12;
    const rowOffset = row - centerRow;
    const colOffset = Math.abs(col - centerCol);
    return rowOffset >= 0 && rowOffset < 7 && colOffset <= rowOffset;
  }

  let grid = Array(GRID_HEIGHT).fill(null).map(() => Array(GRID_WIDTH).fill(false));

  function getCell(row, col) {
    return document.getElementById(`tri-${row}-${col}`);
  }

  function countNeighbors(row, col) {
    let count = 0;
    for (let dr = -1; dr <= 1; dr++) {
      for (let dc = -1; dc <= 1; dc++) {
        if (dr === 0 && dc === 0) continue;
        const nr = row + dr;
        const nc = col + dc;
        if (nr >= 0 && nr < GRID_HEIGHT && nc >= 0 && nc < GRID_WIDTH) {
          if (grid[nr][nc]) count++;
        }
      }
    }
    return count;
  }

  // Standard Conway's Game of Life rules (with slight bias toward target)
  function shouldBeAlive(row, col, neighbors) {
    const isTarget = isTargetCell(row, col);
    const currentlyAlive = grid[row][col];

    // Standard Conway rules
    if (currentlyAlive) {
      // Survival: 2-3 neighbors
      if (neighbors === 2 || neighbors === 3) return true;
    } else {
      // Birth: exactly 3 neighbors
      if (neighbors === 3) return true;
    }

    // Slight bias: target cells with 2 neighbors are more likely to stay alive
    if (isTarget && currentlyAlive && neighbors === 2) {
      return true; // Always keep target cells alive with 2 neighbors
    }

    return false;
  }

  function updateCell(row, col, alive) {
    const cell = getCell(row, col);
    if (!cell) return;

    if (alive) {
      cell.setAttribute('fill', '#7877c6');
      cell.setAttribute('stroke', '#7877c6');
      cell.setAttribute('opacity', '1');
    } else {
      cell.setAttribute('opacity', '0');
    }
  }

  function gameOfLifeStep() {
    const nextGrid = Array(GRID_HEIGHT).fill(null).map(() => Array(GRID_WIDTH).fill(false));

    // Calculate next generation
    for (let row = 0; row < GRID_HEIGHT; row++) {
      for (let col = 0; col < GRID_WIDTH; col++) {
        const neighbors = countNeighbors(row, col);
        nextGrid[row][col] = shouldBeAlive(row, col, neighbors);
      }
    }

    // Update grid and visuals
    for (let row = 0; row < GRID_HEIGHT; row++) {
      for (let col = 0; col < GRID_WIDTH; col++) {
        grid[row][col] = nextGrid[row][col];
        updateCell(row, col, grid[row][col]);
      }
    }
  }

  // Initialize Game of Life after 4 seconds (when purple triangles are shown)
  setTimeout(function() {
    // Set initial state from purple triangles
    initialPurpleTriangles.forEach(([row, col]) => {
      if (row >= 0 && row < GRID_HEIGHT && col >= 0 && col < GRID_WIDTH) {
        grid[row][col] = true;
        updateCell(row, col, true);
      }
    });

    // Start Game of Life simulation
    const interval = setInterval(gameOfLifeStep, 400);

  }, 4000); // Start after 4 seconds

})();
